if(shouldEvent){
rate=15;
}
if(token.getBottom() == -1130 && token.getLeft() == 35){
token.setBottom(-1138);
token.setLeft(123);
}
if(token.getBottom() == -1210 && token.getLeft() == 150){
token.setBottom(-1138);
token.setLeft(123);
}
